<?php/* * Copyright (c) 2023. * RW::Software * Dave Conley * https://www.rwscripts.com/ */$loader = new admin_main();class admin_main{	var $output;	function admin_main()	{		global $IN, $OUTPUT;		switch ($IN['act']) {			case 'home':				$main_content = $this->admin_home();				//$nav = adminNav("", -1);				break;			case 'news':				$main_content = $this->admin_news();				//$nav = adminNav("", -1);				break;			default:				$main_content = $this->admin_home();				//$nav = adminNav("", -1);				break;		}		$OUTPUT->add_output($this->output);	}	function admin_home()	{		global $CONFIG, $updateversion, $version, $sid, $buildnum;		ob_start();		echo admin_head(GETLANG("rwdownload"), GETLANG("acp"));		include_once ROOT_PATH . "/functions/stats.php";		$statinfo = new stats();		echo new_table(-1, "", "", "100%");		echo new_row(-1, "", "", "50%");		echo new_table(-1, "acptablesubhead");		echo GETLANG("stats_newdls");		echo new_row();		echo $statinfo->latestDownloads(7);		echo end_table();		echo new_table(-1, "acptablesubhead");		echo GETLANG("stats_topdls");		echo new_row();		echo $statinfo->topRatedDownloads(7);		echo end_table();		echo new_table(-1, "acptablesubhead");		echo GETLANG("stats_mostdl");		echo new_row();		echo $statinfo->topDownloads(7);		echo end_table();		if (file_exists(ROOT_PATH . "/install/index.php")) {			echo new_table(-1, "acptablesubhead");			echo GETLANG("warn_security");			echo new_row();			echo GETLANG("install_exists");			echo end_table();		}		if (file_exists(ROOT_PATH . "/upgrade/index.php")) {			echo new_table(-1, "acptablesubhead");			echo GETLANG("warn_security");			echo new_row();			echo GETLANG("upgrade_exists");			echo end_table();		}		echo new_col();		echo "<b>Your version:</b> $version<br>";		echo "<b>Build number:</b> $buildnum<br>";		echo "<a href='admin.php?sid=$sid&amp;area=upgrade&amp;act=home' target='body'>" . GETLANG("nav_checkforup") . "</a><br>";		echo end_table();		echo admin_foot();		$this->output = ob_get_contents();		ob_end_clean();	}	function admin_news()	{		$this->output .= admin_head(GETLANG("rwdownload"), GETLANG("news"));		ob_start();		readfile("http://www.rwscripts.com/index.php?ACT=module&name=rwnews");		$this->output .= ob_get_contents();		ob_end_clean();		$this->output .= admin_foot();	}}?>